import React, { Component } from 'react';
import styles from './styles.css';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import MenuItem from '@material-ui/core/MenuItem';
import { connect } from 'react-redux';
import { accountOperations } from '../../../../../../../../state/ducks/account';

const style = theme => ({
    textField: {
        width: '515px'
    },
    menu: {
        width: 200,
    },
});

const ranges = [
    {
        value: 'ROLE_ADMIN',
        label: 'Quản trị viên',
    },
    {
        value: 'ROLE_TEACHER',
        label: 'Giảng viên',
    },
    {
        value: 'ROLE_PARENTS',
        label: 'Phụ huynh',
    },
    {
        value: 'ROLE_STUDENT',
        label: 'Học viên',
    },
];

class FormSign extends Component {
    constructor(props) {
        super(props);
        this.state = {
            login: '',
            password: '',
            re_password: '',
            phone_number: '',
            role: ''
        }
    }

    componentDidUpdate() {
        this.props.updateData(this.state);
    }
    
    handleChange = prop => event => {
        this.setState({[prop]: event.target.value});
    };
    render() {
        const { classes, dataForm } = this.props;
        return (
            <div className={`${styles.formSign}`}>
                <br />
                <div>
                    <div><b>Chú ý:</b></div>
                    <ul>
                        <li>- Các trường thông tin ở dưới là bắt buộc.</li>
                        <li>- Tên tài khoản tối thiểu 5 ký tự là mã số học viên.</li>
                        <li>- Mật khẩu tối thiểu 4 ký tự và tối đa 100 ký tự.</li>
                        <li>- Số điện thoại là các đầu số của VN.</li>
                    </ul>
                </div>
                <div className={`${styles.contentForm}`}>
                    <TextField
                        id="login"
                        label="Mã đăng nhập (*)"
                        type="text"
                        autoComplete="off"
                        className={classes.textField}
                        value={dataForm.login}
                        margin="normal"
                        placeholder="VD: AD42349, GV00012, PH89753, HV56742..."
                        InputLabelProps = {{
                            shrink: true
                        }}
                        variant="outlined"
                        onChange={this.handleChange('login')}
                    />
                    <br />
                    <TextField
                        id="password"
                        label="Mật khẩu (*)"
                        type="password"
                        autoComplete="off"
                        className={classes.textField}
                        value={dataForm.password}
                        margin="normal"
                        placeholder="VD: Meocon@221, Kmaisbest001..."
                        variant="outlined"
                        InputLabelProps = {{
                            shrink: true
                        }}
                        onChange={this.handleChange('password')}
                    />
                    <br />
                    <TextField
                        id="re_password"
                        type="password"
                        label="Nhập lại mật khẩu (*)"
                        placeholder="Xác nhận lại mật khẩu..."
                        autoComplete="off"
                        className={classes.textField}
                        margin="normal"
                        value={dataForm.re_password}
                        InputLabelProps = {{
                            shrink: true
                        }}
                        variant="outlined"
                        onChange={this.handleChange('re_password')}
                    />
                    <br />
                    <TextField
                        id="phone_number"
                        type="text"
                        label="Số điện thoại (*)"
                        className={classes.textField}
                        margin="normal"
                        value={dataForm.phone_number}
                        autoComplete="off"
                        InputLabelProps = {{
                            shrink: true
                        }}
                        placeholder="VD: 0363205500, 0984610992..."
                        variant="outlined"
                        onChange={this.handleChange('phone_number')}
                    />
                    <br />
                    <TextField
                        select
                        id="role"
                        className={classes.textField}
                        variant="outlined"
                        value={dataForm.role}
                        InputLabelProps = {{
                            shrink: true
                        }}
                        onChange={this.handleChange('role')}
                        label="Loại tài khoản (*)"
                        margin="normal"
                    >
                        {ranges.map(option => (
                            <MenuItem key={option.value} value={option.value} style={{padding: 15}}>
                                {option.label}
                            </MenuItem>
                        ))}
                    </TextField>
                </div>
            </div>
        );
    }
}

FormSign.propTypes = {
    classes: PropTypes.object.isRequired,
};

const mapStateToProps = state => ({
    datas : state.account.datas
});

const mapDispatchToProps = {
    updateData: accountOperations.updateDataUserAccount
};

export default connect(mapStateToProps, mapDispatchToProps)(withStyles(style)(FormSign));
