import React from 'react';
import classNames from 'classnames';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import IconButton from '@material-ui/core/IconButton';
import Tooltip from '@material-ui/core/Tooltip';
import Button from '@material-ui/core/Button';
import DeleteIcon from '@material-ui/icons/DeleteSweepRounded';
import GetAppIcon from '@material-ui/icons/GetAppRounded';
import SearchIcon from '@material-ui/icons/SearchRounded';
import CloudUploadIcon from '@material-ui/icons/CloudUpload';
import LocalPrintshopRoundedIcon from '@material-ui/icons/LocalPrintshopRounded';
import PermContactCalendarIcon from '@material-ui/icons/PermContactCalendar';
import FilterListIcon from '@material-ui/icons/FilterList';
import { lighten } from '@material-ui/core/styles/colorManipulator';

const toolbarStyles = theme => ({
    root: {
        paddingRight: theme.spacing.unit,
    },
    highlight:
        theme.palette.type === 'light'
            ? {
                color: theme.palette.secondary.main,
                backgroundColor: lighten(theme.palette.secondary.light, 0.85),
            }
            : {
                color: theme.palette.text.primary,
                backgroundColor: theme.palette.secondary.dark,
            },
    spacer: {
        flex: '1 1 100%',
    },
    actions: {
        color: theme.palette.text.secondary,
    },
    title: {
        flex: '0 0 auto',
    },
    button: {
        margin: theme.spacing.unit,
        fontSize: '11px'
    },
    leftIcon: {
        marginRight: theme.spacing.unit,
    },
    rightIcon: {
        marginLeft: theme.spacing.unit,
    },
    iconSmall: {
        fontSize: 20,
    },
});

let EnhancedTableToolbar = props => {
    const { numSelected, classes, listName, rows } = props;
    return (
        <Toolbar
            className={classNames(classes.root, {
                [classes.highlight]: false,
            })}
        >
            <div className={classes.title}>
                {numSelected > 0 ? (
                    <Typography variant="subtitle1">
                        <span style={{ fontWeight: 200, fontSize: 14 }}>{numSelected} rows(s) selected</span>
                    </Typography>
                ) : (
                        <Typography variant="h6" id="tableTitle" style={{ fontWeight: 200, fontSize: 20, lineHeight: '35px' }}>
                            <PermContactCalendarIcon />  { listName }
                        </Typography>
                    )}
            </div>
            <div className={classes.spacer} />
            <div className={classes.actions} style={{ width: '100%', textAlign: 'right' }}>
                {numSelected > 0 ? (
                    <Tooltip title="Xóa">
                        <Button variant="contained" color="secondary" className={classes.button}>
                            Delete
                            <DeleteIcon className={classes.rightIcon} />
                        </Button>
                    </Tooltip>
                ) : (
                        <div>
                            <Tooltip title="Tìm kiếm">
                                <IconButton aria-label="Tìm kiếm">
                                    <SearchIcon />
                                </IconButton>
                            </Tooltip>
                            <Tooltip title="Tải về Excel">
                                <IconButton aria-label="Download Excel">
                                    <GetAppIcon />
                                </IconButton>
                            </Tooltip>
                            <Tooltip title="In danh sách">
                                <IconButton aria-label="Print">
                                    <LocalPrintshopRoundedIcon />
                                </IconButton>
                            </Tooltip>
                            <Tooltip title="Lọc danh sách">
                                <IconButton aria-label="Lọc danh sách">
                                    <FilterListIcon />
                                </IconButton>
                            </Tooltip>
                            <Tooltip title="Tải lên excel">
                                <Button variant="contained" color="inherit" className={classes.button} title="Tải lên excel">
                                    Tải lên
                                <CloudUploadIcon className={classes.rightIcon} />
                                </Button>
                            </Tooltip>
                        </div>
                    )}
            </div>
        </Toolbar>
    );
};

EnhancedTableToolbar.propTypes = {
    classes: PropTypes.object.isRequired,
    numSelected: PropTypes.number.isRequired,
    listName: PropTypes.string.isRequired,
};


export default withStyles(toolbarStyles)(EnhancedTableToolbar);
