import * as types from "./types";

export const getAllClassByCourseId = (id) => ( {
    type: types.GET_ALL_CLASS_BY_ID_COURSE,
    meta: {
        async: true,
        blocking: true,
        path: `/class/course/${id}`,
        method: "GET"
    }
} );

export const getClassById = ( id ) => ( {
    type: types.GET_CLASS_BY_ID,
    meta: {
        async: true,
        blocking: true,
        path: `/class/${id}`,
        method: "GET"
    }
} );

