import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Stepper from '@material-ui/core/Stepper';
import Step from '@material-ui/core/Step';
import StepLabel from '@material-ui/core/StepLabel';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import { FormSign } from './components/FormSign';
import { connect } from 'react-redux';
import { accountOperations } from '../../../../../../state/ducks/account';

const styles = theme => ({
    root: {
        width: '100%',
        marginTop: '24px',
        padding: '0 24px 24px 24px'
    },
    button: {
        marginRight: theme.spacing.unit,
    },
    instructions: {
        marginTop: theme.spacing.unit,
        marginBottom: theme.spacing.unit,
        padding: '20px 12px'
    },
});

function getSteps() {
    return ['Đăng ký tài khoản', 'Chi tiết tài khoản', 'Kết thúc'];
}

function getStepContent(step, datas) {
    switch (step) {
        case 0:
            return (
                <div>
                    <span><b>Bước 1</b>: Điền đầy đủ thông tin đăng nhập vào các trường ở phía dưới.</span>
                    <FormSign dataForm={datas} />
                </div>
            );
        case 1:
            return 'What is an ad group anyways?';
        case 2:
            return 'This is the bit I really care about!';
        default:
            return 'Unknown step';
    }
}

class Steppers extends React.Component {
    state = {
        activeStep: 0,
        skipped: new Set(),
        errors: {
            login: [],
            password: [],
            re_password: [],
            phone_number: [],
            role: []
        }
    };

    isStepOptional = step => step === 1;

    handleNext = () => {
        const { activeStep } = this.state;
        const { datas } = this.props;
        this.isErrors(datas);
        let { skipped } = this.state;
        if (this.isStepSkipped(activeStep)) {
            skipped = new Set(skipped.values());
            skipped.delete(activeStep);
        }
        this.setState({
            activeStep: activeStep + 1,
            skipped,
        });
    };

    handleBack = () => {
        this.setState(state => ({
            activeStep: state.activeStep - 1,
        }));
    };

    handleSkip = () => {
        const { activeStep } = this.state;
        if (!this.isStepOptional(activeStep)) {
            throw new Error("You can't skip a step that isn't optional.");
        }

        this.setState(state => {
            const skipped = new Set(state.skipped.values());
            skipped.add(activeStep);
            return {
                activeStep: state.activeStep + 1,
                skipped,
            };
        });
    };

    handleReset = () => {
        this.setState({
            activeStep: 0,
        });
    };

    isStepSkipped(step) {
        return this.state.skipped.has(step);
    }

    isErrors = datas => {
        let login = '';
        let password = '';
        let re_password = '';
        let phone_number = '';
        let role = '';
        const { activeStep } = this.state;
        let count = 0;
        if (activeStep === 0) {
            if (datas.login === '') {
                login = "Mã đăng nhập không được để trống!";
            } else if (!/((AD|PH|GV|HV)+([0-9]{5})\b)/.test(datas.login)) {
                login = "Mã đăng nhập không hợp lệ!";
            } else {
                login = "";
            }

            if (datas.password === '') {
                password = "Mật khẩu không được để trống!";
            } else if ((datas.password.length < 4) || (datas.password.length > 100)) {
                password = "Mật khẩu phải lớn hơn 3 ký tự và bé hơn 101 ký tự!";
            } else {
                password = "";
            }

            if (datas.re_password === '') {
                re_password = "Mật khẩu nhập lại không được để trống!";
            } else if (datas.re_password !== datas.password) {
                re_password = "Mật khẩu nhập lại không khớp!";
            } else {
                re_password = "";
            }

            if (datas.role === '') {
                role = "Chưa chọn loại tài khoản!";
            } else {
                switch (datas.role) {
                    case "ROLE_ADMIN":
                        if (datas.login !== '' && datas.login.substring(0, 2) !== 'AD') {
                            role = "Loại tài khoản không khớp với mã đăng nhập!";
                        } else {
                            role = "";
                        }
                        break;
                    case "ROLE_TEACHER":
                        if (datas.login !== '' && datas.login.substring(0, 2) !== 'GV') {
                            role = "Loại tài khoản không khớp với mã đăng nhập!";
                        } else {
                            role = "";
                        }
                        break;
                    case "ROLE_PARENTS":
                        if (datas.login !== '' && datas.login.substring(0, 2) !== 'PH') {
                            role = "Loại tài khoản không khớp với mã đăng nhập!";
                        } else {
                            role = "";
                        }
                        break;
                    case "ROLE_STUDENT":
                        if (datas.login !== '' && datas.login.substring(0, 2) !== 'HV') {
                            role = "Loại tài khoản không khớp với mã đăng nhập!";
                        } else {
                            role = "";
                        }
                        break;
                }
            }

        }
        console.log('login: ' + login + ', password: ' + password + ', re_password: ' + re_password + ', role: ' + role);

    }

    render() {
        const { classes, datas } = this.props;
        const steps = getSteps();
        const { activeStep } = this.state;
        return (
            <div className={classes.root}>
                <Stepper activeStep={activeStep} style={{ backgroundColor: '#f9f9f9' }}>
                    {steps.map((label, index) => {
                        const props = {};
                        const labelProps = {};
                        if (this.isStepSkipped(index, datas)) {
                            props.completed = false;
                        }
                        return (
                            <Step key={label} {...props}>
                                <StepLabel {...labelProps}>{label}</StepLabel>
                            </Step>
                        );
                    })}
                </Stepper>
                <div>
                    <Typography className={classes.instructions}>{getStepContent(activeStep, datas)}</Typography>
                    <div>
                        <Button
                            variant="contained"
                            color="primary"
                            onClick={this.handleNext}
                            className={classes.button}
                        >
                            {activeStep === 0 ? 'Tạo tài khoản' : ''}
                            {activeStep === 1 ? 'Hoàn tất thông tin' : ''}
                            {activeStep === 2 ? 'Kết thúc' : ''}
                        </Button>
                    </div>
                </div>
            </div>
        );
    }
}

Steppers.propTypes = {
    classes: PropTypes.object,
};

const mapStateToProps = state => ({
    datas: state.account.datas
});

export default connect(mapStateToProps, null)(withStyles(styles)(Steppers));
